import React, { Component } from 'react';
import { Card, CardBody, CardHeader, Progress, Row, Col, Button, Form,
    FormGroup,
    FormText,
    FormFeedback,
    Input,
    InputGroup,
    InputGroupAddon,
    InputGroupText,
    Label } from 'reactstrap';
import { AppSwitch } from '@coreui/react'
import { throws } from 'assert';

import servicesData from './ServicesData'


function ServiceCol(props) {
    const service = props.service
    return (
      <Col xs="12" sm="6" lg="3">

        <Card>
            <CardHeader>
                <div className="card-header-actions">
                  <AppSwitch id="HDP" className={'mx-1'} variant={'pill'} color={'success'} outline={'alt'} size={'sm'} />
                </div>
            </CardHeader>
            <CardBody>
                <table>
                    <tr>
                        <td><img src={service.img}/></td>
                    </tr>
                </table>
                <div className="card-header-actions">
                  <AppSwitch id="HDP" className={'mx-1'} variant={'pill'} color={'success'} outline={'alt'} size={'sm'} />
                </div>
            </CardBody>
        </Card>
       </Col>
    )
  }

class Services extends Component{
    constructor(props) {
        super(props);

        this.state = {
            selectedServices: '',
            servicesDependency: ''
        };
      }


    saveAndContinue = (e) => {
        e.preventDefault();
        this.props.nextStep();
    }

    back  = (e) => {
        e.preventDefault();
        this.props.prevStep();
    }

    loading = () => <div className="animated fadeIn pt-1 text-center">Loading...</div>

    render(){
        const {values: { clusterType, clusterVersion, clusterId }} = this.props;

        const serviceList = servicesData.filter((service) => service.id)

        return(

            <div className="animated fadeIn">
                <Row>
                    {serviceList.map((service, index) =>
                      <ServiceCol key={index} service={service}/>
                    )}
                </Row>
             </div>
        )
    }
}

export default Services;
