import React, { Component } from 'react';
import { Card, CardBody, CardColumns, CardHeader } from 'reactstrap';

import libraryData from './LibraryData'



class LibraryItem extends Component {

  render() {
    const libraryItem = libraryData.find( libraryItem => libraryItem.id.toString() === this.props.match.params.id)

    const libraryItemDetails = libraryItem ? Object.entries(libraryItem) : [['id', (<span><i className="text-muted icon-ban"></i> Not found</span>)]]

    return (
      <div className="animated fadeIn">
      <CardColumns className="cols-2">
        <Card>
          <CardHeader>
            Overview
            <div className="card-header-actions">
              <a href="http://www.chartjs.org" className="card-header-action">
                <small className="text-muted">docs</small>
              </a>
            </div>
          </CardHeader>
          <CardBody>
            <div className="chart-wrapper" align="center">
              <h1>{libraryItem.name.toString()} Bundle</h1>
              <img src={libraryItem.image}/>
            </div>
          </CardBody>
        </Card>
      </CardColumns>
    </div>
    )
  }
}

export default LibraryItem;
