import React, { Component } from 'react';
import UserDetails from './UserDetails';
import PersonalDetails from './PersonalDetails';
import Confirmation from './Confirmation';
import Success from './Success';
import WelcomeScreen from './WelcomeScreen';
import ClusterType from './ClusterType';

class Generator extends Component {
    state = {
        step: 1,
        services: '',
        recipes: '',
        hdpSwitch: true,
        hdfSwitch: false,
        combinedSwitch: false,
        hdpBorder: 'border-success',
        hdfBorder: '',
        combinedBorder: '',
        hdpHeader: 'text-white bg-success',
        hdfHeader: '',
        combinedHeader: '',
        clusterVersion: '',
        clusterType: 'HDP'
    }

    changeSwitch = (e) => {
        var eader = '';
        var border = '';
        var cluster ='';
        if (e.target.id == "HDF") {
            if (this.state.hdfSwitch == false) {
                border = 'border-success';
                eader = 'text-white bg-success';
                cluster = "HDF";
            }
            this.setState({
                hdpSwitch: false,
                hdfSwitch: !this.state.hdfSwitch,
                combinedSwitch: false,
                hdpBorder: '',
                hdfBorder: border,
                combinedBorder: '',
                hdpHeader: '',
                hdfHeader: eader,
                combinedHeader: '',
                clusterType: cluster,
            })
        } else if (e.target.id == "HDP") {
            if (this.state.hdpSwitch == false) {
                border = 'border-success';
                eader = 'text-white bg-success';
                cluster = "HDP";
            }
            this.setState({
                hdpSwitch: !this.state.hdpSwitch,
                hdfSwitch: false,
                combinedSwitch: false,
                hdpBorder: border,
                hdfBorder: '',
                combinedBorder: '',
                hdpHeader: eader,
                hdfHeader: '',
                combinedHeader: '',
                clusterType: cluster,
            })
        } else if (e.target.id == "COMBINED") {
            if (this.state.combinedSwitch == false) {
                border = 'border-success';
                eader = 'text-white bg-success';
                cluster = "COMBINED";
            }
            this.setState({
                hdpSwitch: false,
                hdfSwitch: false,
                combinedSwitch: !this.state.combinedSwitch,
                hdpBorder: '',
                hdfBorder: '',
                combinedBorder: border,
                hdpHeader: '',
                hdfHeader: '',
                combinedHeader: eader,
                clusterType: cluster,
            })

        }

    }

    nextStep = () => {
        const { step } = this.state
        this.setState({
            step : step + 1
        })
    }

    prevStep = () => {
        const { step } = this.state
        this.setState({
            step : step - 1
        })
    }

    handleChange = input => event => {
        this.setState({ [input] : event.target.value })
    }

    changeClusterType = input => event =>  {
         this.setState({ clusterType : input })
    }

    render(){
        const {step} = this.state;
        const { clusterVersion, clusterType, services, recipes } = this.state;
        const values = { clusterVersion, clusterType, services, recipes };
        switch(step) {
        case 1:
            return <WelcomeScreen 
                    nextStep={this.nextStep} 
                    handleChange = {this.handleChange}
                    values={values}
                    />
        case 2:
            return <ClusterType 
                    nextStep={this.nextStep}
                    prevStep={this.prevStep}
                    changeSwitch = {this.changeSwitch}
                    handleChange = {this.handleChange}
                    values={values}
                    />
        case 3:
            return <Confirmation 
                    nextStep={this.nextStep}
                    prevStep={this.prevStep}
                    values={values}
                    />
        case 4:
            return <Success />
        }
    }
}

   export default Generator;
