import React, { Component } from 'react';
import { Form } from 'semantic-ui-react';
import { Card, CardBody, CardHeader, Col, Row, Button, ListGroup, ListGroupItem } from 'reactstrap';

class WelcomeScreen extends Component{

    saveAndContinue = (e) => {
        e.preventDefault()
        this.props.nextStep()
    }

    render(){
        const { values } = this.props;
        return(

            <div className="animated fadeIn">

            <Row>
                <Col>
                <Card>
        <CardHeader className="text-white bg-success">
        <h1>Welcome to Bundle Generator!</h1>

          </CardHeader>
          <CardBody >
            <div className="chart-wrapper" align="left">
            <p>Creating a combination of Cloudbreak blueprints and recipes is easy with Cloudbreak Cuisine.</p>
                    <p>Follow these 4 simple steps:</p>
                    <ListGroup>

                        <ListGroupItem>Step 1: Choose a cluster type</ListGroupItem>
                        <ListGroupItem>Step 2: Select the services you need</ListGroupItem>
                        <ListGroupItem>Step 3: Select custom add-ons for these services</ListGroupItem>
                        <ListGroupItem>Step 4: Enter cluster details</ListGroupItem>

                    </ListGroup>
                <Button size="lg" color="primary" onClick={this.saveAndContinue}>
                  Get Started  <i className="fa fa-long-arrow-right"></i>
                </Button>
            </div>
          </CardBody>
        </Card>


                </Col>
            </Row>
            </div>

        )
    }
}

export default WelcomeScreen;
