import React, { Component } from 'react';
import { Form, Button } from 'semantic-ui-react';
import { Card, CardBody, CardHeader, Progress, Row, Col } from 'reactstrap';
import { throws } from 'assert';

class ClusterType extends Component{
    saveAndContinue = (e) => {
        e.preventDefault();
        this.props.nextStep();
    }

    back  = (e) => {
        e.preventDefault();
        this.props.prevStep();
    }

    render(){
        const { values } = this.props
        return(
            <div className="animated fadeIn">
                    <Row>
                <Col>
        <h1>Step 1: Choose a cluster type</h1>
        </Col>
        </Row>
            <Row>
                <Col>
        <Progress  value='25' color="success" text-align="center" size="lg">25%</Progress>
        </Col>
        </Row>

        <Row>
        <Col>
        <Form color='blue' >
            <h1 className="ui centered">Enter Personal Details</h1>
            <Form.Field>
                <label>Age</label>
                <input placeholder='Age'
                onChange={this.props.handleChange('age')}
                defaultValue={values.age}
                />
            </Form.Field>
            <Form.Field>
                <label>City</label>
                <input placeholder='City'
                onChange={this.props.handleChange('city')}
                defaultValue={values.city}
                />
            </Form.Field>
            <Form.Field>
                <label>Country</label>
                <input placeholder='Country'
                onChange={this.props.handleChange('country')}
                defaultValue={values.country}
                />
            </Form.Field>
        </Form>
        </Col>
        </Row>
        <Row>
                <Col>
                <div className="chart-wrapper" align="left">
                <Button size="lg" color="primary" onClick={this.back}>
                <i className="fa fa-long-arrow-left"></i> Back  
                </Button>
                </div>
                </Col>
                <Col>
                <div className="chart-wrapper" align="right">
                <Button size="lg" color="primary" onClick={this.saveAndContinue}>
                  Next  <i className="fa fa-long-arrow-right"></i>
                </Button>
                </div>
                </Col>
            </Row>
        </div>
        )
    }
}

export default ClusterType;
