import React, { Component } from 'react';
import { Card, CardBody, CardHeader, Progress, Row, Col, Button, Form,
    FormGroup,
    FormText,
    FormFeedback,
    Input,
    InputGroup,
    InputGroupAddon,
    InputGroupText,
    Label,
    Table, } from 'reactstrap';
import { AppSwitch } from '@coreui/react'
import { throws } from 'assert';

import recipesData from './RecipesData'


class Recipes extends Component{
    constructor(props) {
        super(props);
        this.toggleCheckbox = this.toggleCheckbox.bind(this);

        this.state = {firstLoad: true}

    }



    saveAndContinue = (e) => {


        e.preventDefault();
        this.props.nextStep();
    }

    back  = (e) => {
        e.preventDefault();
        this.props.prevStep();
    }

    toggleCheckbox = (e) => {

    }

    loading = () => <div className="animated fadeIn pt-1 text-center">Loading...</div>

    render(){
        const {values: { services }} = this.props;

        const serviceList = services.filter((service) => service.id);
        const allRecipes = recipesData.filter((recipe) => recipe.id);

        return(  

            <div className="animated fadeIn align-items-center">
                <Row>
                    <Col>
                        <h1>Step 3: Select Add-Ons</h1>
                    </Col>
                </Row>
                <Row>
                    <Col>

                        &nbsp; 
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <Progress animated value='60' color="dark" text-align="center" size="lg"></Progress>
                    </Col>
                </Row>
                <Row>
                    <Col>
                        &nbsp; 
                    </Col>
                </Row>
                <Row>
                </Row>
                <Row><Table hover responsive className="table-outline mb-0 d-none d-sm-table">
                  <thead className="thead-light">
                  <tr>
                    <th className="text-center"><i className="icon-cog"></i></th>
                    <th>Service</th>
                    <th className="text-center">Add-on Type</th>
                    <th>Description</th>
                    <th className="text-center">Recipe Type</th>
                    <th className="text-center"><i className="fa fa-check"></i></th>
                  </tr>
                  </thead>
                  <tbody>


                    {serviceList.map((service) => {
                            //return <h1>{service.id}</h1>
                            const associatedRecipes = allRecipes.filter((recipe) => (recipe.serviceId == service.id))
                            return associatedRecipes.map((recipe) => {
                                   return  <tr>
                                   <td className="text-center">
                                     <div>
                                       <img src={service.img} />
                                     </div>
                                   </td>
                                   <td>
                                     <div>{service.description}</div>
                                   </td>
                                   <td>
                                    <div>{recipe.addon_type}</div>
                                   </td>
                                   <td>
                                    <div>{recipe.recipeDescription}</div>
                                   </td>
                                   <td className="text-center">
                                   <div>{recipe.recipe_type}</div>
                                   </td>
                                   <td className="text-center">
                                    <FormGroup check className="checkbox">
                                            <Input className="form-check-input" type="checkbox" id="checkbox1" name="checkbox1" value="option1" />
                                        </FormGroup>
                                   </td>
                                 </tr>
                             }
                            )
                        })
                    }
                               </tbody>
                </Table>
                </Row>
                <Row>
                    <Col>
                        <div className="chart-wrapper" align="left">
                            <Button size="lg" outline color="primary"  onClick={this.back}>
                                <i className="fa fa-long-arrow-left"></i> Back  
                            </Button>
                        </div>
                    </Col>
                    <Col>
                        <div className="chart-wrapper" align="right">
                            <Button id={this.state.clusterType} size="lg" color="primary" onClick={this.saveAndContinue} disabled={this.state.nextDisabled}>
                                Next  <i className="fa fa-long-arrow-right"></i>
                            </Button>
                        </div>
                    </Col>
                </Row>
            </div>     
        )
    }
}

export default Recipes;
