import React, { Component } from 'react';
import { Card, CardBody, CardHeader, Progress, Row, Col, Button, Form,
    FormGroup,
    FormText,
    FormFeedback,
    Input,
    InputGroup,
    InputGroupAddon,
    InputGroupText,
    Label,
    Table, } from 'reactstrap';
import { AppSwitch } from '@coreui/react'
import { throws } from 'assert';

import servicesData from './ServicesData'


function ServiceCol(props) {
    const service = props.service
    return (
      <Col md="2">

        <Card hover responsive>
            <CardBody className='bg-white no-border'>
            <img src={service.img} height="25px" width="25px"/> &nbsp; &nbsp; {service.description}
            <div className="float-right" align="bottom">
                <AppSwitch id="HDP" className={'mx-1'} variant={'pill'} color={'success'}  />
            </div>
            </CardBody>
        </Card>
       </Col>
            // <Table hover responsive className="table-outline mb-0 d-none d-sm-table">
            //       <tbody>
            //         <tr>
            //             <td className="text-center">
            //             <div>
            //                 <img src={service.img} height="50px" width="50px"/>
            //             </div>
            //             </td>
            //             <td>
            //             <div>{service.description}</div>
            //             <div className="small text-muted">
            //                 Some service description
            //             </div>
            //             </td>
            //             <td className="text-center">
            //             <AppSwitch id="HDP" className={'mx-1'} variant={'pill'} color={'success'}/>
            //             </td>
            //             </tr>
            //         </tbody>
            //     </Table>
    )
  }

class Services extends Component{
    constructor(props) {
        super(props);

        this.state = {
            selectedServices: '',
            servicesDependency: ''
        };
      }


    saveAndContinue = (e) => {
        e.preventDefault();
        this.props.nextStep();
    }

    back  = (e) => {
        e.preventDefault();
        this.props.prevStep();
    }

    loading = () => <div className="animated fadeIn pt-1 text-center">Loading...</div>

    render(){
        const {values: { clusterType, clusterVersion, clusterId }} = this.props;

        const cluster_id={clusterId};

        const serviceList = servicesData.filter((service) => service.cluster_id == 1); 

        return(

            <div className="animated fadeIn align-items-center">
            <p>{clusterId} </p>
                <Row>
                    {serviceList.map((service) =>
                      <ServiceCol service={service}/>
                    )}
                </Row>
                <Row>
             <Col>
             <div className="chart-wrapper" align="left">
             <Button size="lg" outline color="primary"  onClick={this.back}>
             <i className="fa fa-long-arrow-left"></i> Back  
             </Button>
             </div>
             </Col>
             <Col>
             <div className="chart-wrapper" align="right">
             <Button id={this.state.clusterType} size="lg" color="primary" onClick={this.saveAndContinue} disabled={this.state.nextDisabled}>
               Next  <i className="fa fa-long-arrow-right"></i>
             </Button>
             </div>
             </Col>
         </Row>
             </div>

        )
    }
}

export default Services;
